import React from 'react';
import './FeaturedMove.css'

export default function FeaturedMove({ featuredData }) {

    const firstDate = new Date(featuredData.first_air_date);
    const genresList = featuredData.genres.map((elem) => (elem.name));

    return (
       <section className='featured' style={{
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundImage: `url(https://image.tmdb.org/t/p/original${featuredData.backdrop_path})`
       }}>

        <div className='featured--vertical'>
            <div className='featured--horizontal'>
                <div className='featured--name'>{featuredData.original_name}</div>
                <div className='featured--info'>
                    <div className='featured--points'>{featuredData.vote_average}</div>
                    <div className='featured--year'>{firstDate.getFullYear()}</div>
                    <div className='featured--seasons'>{featuredData.number_of_seasons} temporadas</div>
                </div>

                <div className='featured--desciption'>{featuredData.overview}</div>

                <div className='featured--buttons'>

                </div>

                <div className='featured--genres'>
                    <strong>GÃªneros: </strong>
                    { genresList.join(', ')}
                </div>
            </div>
        </div>



       </section>
    )
}
